import React,{useEffect} from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';
import {toast} from 'react-toastify';
import { Button, Container, Row, Col, Alert, Table, Navbar, Nav } from 'react-bootstrap';
class Home extends React.Component{

  state={
    patientData:[]
  }
    
 

      componentDidMount(){
        axios.get('http://localhost:8080/clinicalservices/api/patients').then(res=>{
          const patientData = res.data;
          this.setState({patientData})

        })

      }


    
      

      render(){
        var variant="";
        return (
        <div>
          
              <div>
              <Navbar bg="dark">
                  <Navbar.Brand href="/">
                    <img
                      src="/logo.svg"
                      width="30"
                      height="30"
                      className="d-inline-block align-top"
                      alt="React Bootstrap logo"
                    />
                    <Nav className="justify-content-end">
                        <Nav.Item>
                      <Nav.Link href="/home">Active</Nav.Link>
                    </Nav.Item>
                    </Nav>
                  </Navbar.Brand>
            </Navbar>
            </div>     
        <Container>
        <Row className="justify-content-md-center">  
        <h2>Patient:</h2>
        </Row>
        <Row className="justify-content-md-center">
          <Col md={9}>
        <Table striped bordered hover size="sm" variant="dark">
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Age</th>
              <th colSpan="3">Change Data</th>
            </tr>
          </thead> 
          <tbody>
            {this.state.patientData.map(patient=><RowCreator item={patient} key={patient.id} />)}
          </tbody>
        </Table>
        </Col>
        </Row>
        </Container> 
        <Alert variant="dark">
        <Link to={"/addPatient"}><h6 style={{ color: 'black' }}>Register Patient</h6></Link>
        </Alert>
        </div>
        )
      }
    }

    class RowCreator extends React.Component {
      
      render() {

      
      function povikaj(id){

     
      
        axios.get('http://localhost:8080/clinicalservices/api/patients/delete/'+id).then(res=>{
          toast("Patient Deleted Sucessfully, pls wait 3 seconds",{autoClose:3000,position:toast.POSITION.BOTTOM_CENTER})
          
          setTimeout(function() {
            window.location.reload();
            }.bind(this), 3000);
        })
        
             
      }

       

    var patient = this.props.item;

    return (
      <tr>
        <td>{patient.firstName}</td>
        <td>{patient.lastName}</td>
        <td>{patient.age}</td>
        <td>
          <Link to={"/patientDetails/" + patient.id}><Button variant="info">Add Data</Button></Link>
        </td>
        <td>
          <Link to={"/analyze/" + patient.id}><Button variant="info">Analyze</Button></Link>
        </td>
        <td>
        <Button variant="info" onClick={()=>povikaj(patient.id)}>Delete</Button>
        </td>
      </tr>
    );
  }
}

export default Home;
