import React, { Component } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import { isEmail } from "validator";
import { Button, Container, Row, Col, Alert, Table, Navbar, Nav, NavDropdown, Carousel } from 'react-bootstrap';
import AuthService from "../services/auth.service";
import Figure from 'react-bootstrap/Figure'
import slikazalogin from '../assets/iamges/default.png'
import '../css/register.css'
const required = value => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

const email = value => {
  if (!isEmail(value)) {
    return (
      <div className="alert alert-danger" role="alert">
        This is not a valid email.
      </div>
    );
  }
};

const vusername = value => {
  if (value.length < 3 || value.length > 20) {
    return (
      <div className="alert alert-danger" role="alert">
        The username must be between 3 and 20 characters.
      </div>
    );
  }
};

const vpassword = value => {
  if (value.length < 6 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The password must be between 6 and 40 characters.
      </div>
    );
  }
};

const vpasswordconfirm = value => {
  if (value.length < 6 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The password must be between 6 and 40 characters.
      </div>
    );
  }
};

export default class Register extends Component {
  constructor(props) {
    super(props);
    this.handleRegister = this.handleRegister.bind(this);
    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);
    this.onChangeConfirmPassword = this.onChangeConfirmPassword.bind(this);

    this.state = {
      username: "",
      email: "",
      password: "",
      confirmpassword: "",
      successful: false,
      message: ""
    };
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    });
  }

  onChangeEmail(e) {
    this.setState({
      email: e.target.value
    });
  }

  onChangePassword(e) {
    this.setState({
      password: e.target.value
    });
  }

  onChangeConfirmPassword(e){
    this.setState({
      confirmpassword: e.target.value
    })
  }

  handleRegister(e) {
    e.preventDefault();

    this.setState({
      message: "",
      successful: false
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      if(this.state.password!==this.state.confirmpassword){
        this.setState({
          message: "Passwords don't match"
        })
      }else{
      AuthService.register(
        this.state.username,
        this.state.email,
        this.state.password
      ).then(
        response => {
          this.setState({
            message: response.data.message,
            successful: true
          });
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          this.setState({
            successful: false,
            message: resMessage
          });
        }
      );
    }
    }
  }

  render() {
    return (
      
      <div class="za-register">
          <Form
            onSubmit={this.handleRegister}
            ref={c => {
              this.form = c;
            }}
          >
             <Container fluid>
            <Row className="justify-content-md-center mt-1">
            
            <div id="circle"></div>
            <div id="za-text">
              Neno's
            </div>   
            <div id="circle1"></div> 
            </Row>
            <Row className="justify-content-md-center">
              <div id="za-text2">
                Clinic
              </div>
            </Row>    
            </Container>
            
            {!this.state.successful && (
              <div>
                <Row className="justify-content-center mt-3">
                <div className="form-group col-2" id="username-register">
                  <label htmlFor="username">Username</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="username"
                    value={this.state.username}
                    onChange={this.onChangeUsername}
                    validations={[required, vusername]}
                  />
                </div>
                </Row>

                <Row className="justify-content-center mt-1">
                <div className="form-group col-2" id="email-register">
                  <label htmlFor="email">Email</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="email"
                    value={this.state.email}
                    onChange={this.onChangeEmail}
                    validations={[required, email]}
                  />
                </div>
                </Row>

                <Row className="justify-content-center mt-1">
                <div className="form-group col-2" id="password-register">
                  <label htmlFor="password">Password</label>
                  <Input
                    type="password"
                    className="form-control"
                    name="password"
                    value={this.state.password}
                    onChange={this.onChangePassword}
                    validations={[required, vpassword]}
                  />
                </div>
                </Row>
            
                <Row className="justify-content-center mt-1">
                <div className="form-group col-2" id="username-register">
                  <label htmlFor="confirmpassword">Confirm Password</label>
                  <Input
                    type="password"
                    className="form-control"
                    name="confirmpassword"
                    value={this.state.confirmpassword}
                    onChange={this.onChangeConfirmPassword}
                    validations={[required, vpasswordconfirm]}
                    
                  />
                </div>
                </Row>

                <Row className="justify-content-center mt-1">
                <div className="form-group col-2">
                  <button className="btn btn-primary btn-block">Sign Up</button>
                </div>
                </Row>
              </div>
              
            )}

            {this.state.message && (
              <Row className="justify-content-center mt-1">
              <div className="form-group col-2">
                <div
                  className={
                    this.state.successful
                      ? "alert alert-success"
                      : "alert alert-danger"
                  }
                  role="alert"
                >
                  {this.state.message}
                </div>
              </div>
              </Row>
            )}
            <CheckButton
              style={{ display: "none" }}
              ref={c => {
                this.checkBtn = c;
              }}
            />
          </Form>
          </div>
    );
  }
}
