import React from 'react';
import axios from 'axios';
import CanvasJSReact from '../assets/canvasjs.react';
//var CanvasJSReact = require('./canvasjs.react');
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;
class ChartGenerator extends React.Component{

    state={
        clinicalData:[]
    }

    

    

    componentDidMount(){
        axios.get("http://localhost:8080/clinicalservices/api/clinicals/"+this.props.match.params.patientId+"/"+this.props.match.params.componentName).then(res=>{

            this.setState({ clinicalData: res.data });
        })

    }


    render(){
        var dps1=[];
        var dps = []; 
        var Vreme = new Array();
        var myArray = new Array();
        this.state.clinicalData.map((item,index)=>{
            myArray.push(item.componentValue);
            Vreme.push(item.measuredDateTime)

        })
        console.log(Vreme);
        var VisokPritisok = new Array();
        var NizokPritisok = new Array();
        console.log(myArray)
        var visok=myArray.join().toString();
        console.log(visok)
        myArray.forEach(element => 
            VisokPritisok.push(element.split('/')[0])
            );

        myArray.forEach(element => 
            NizokPritisok.push(element.split('/')[1])
                 );

        console.log("Visok pritisok "+VisokPritisok);
        console.log("Nizok pritisok "+NizokPritisok);
        VisokPritisok.forEach(element =>
            dps.push({y:element,label:""})
        )
        NizokPritisok.forEach(element=>
            dps1.push({y:element,label:""}))

        var newOjbect = dps.map((item, i) => Object.assign({}, item, {label:Vreme[i]}));
        var newOjbect1 = dps1.map((item, i) => Object.assign({}, item, {label:Vreme[i]}));
        console.log(newOjbect);
        console.log(newOjbect1);
        const options = {
            
            animationEnabled: true,	
            title:{
                text: "Number of New Customers"
            },
            
            toolTip: {
                shared: true
            },
            data: [
            {
                type: "line",
                name: "2017",
                showInLegend: true,
                dataPoints:dps1
            }]
    }

        return (<div>
            
            <h2>Clinical Report:</h2>
            {this.state.clinicalData.map((item,index)=>(
                
                <p key={index}>{item.componentValue}</p>
                
            ))}
             <CanvasJSChart options = {options} 
				 onRef={ref => this.chart = ref}
			/>

            

        </div>);
    }
}



export default ChartGenerator;
