import React from 'react';
import axios from 'axios';
import {toast} from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'
import {Link} from 'react-router-dom';

toast.configure();

class AddPatient extends React.Component{
    handleSubmit(event){
        event.preventDefault();
        const data={
            firstName:this.firstName,
            lastName:this.lastName,
            age:this.age
        }

        axios.post('http://localhost:8080/clinicalservices/api/patients',data).then(res=>{
            toast("Patient added sucessfully!",{autoClose:2000,position:toast.POSITION.BOTTOM_CENTER})
        })


    }
    render(){
        return (<div>

                <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
                     <Navbar.Brand className="mx-5" href="/" style={{ color: '#5bc0de'}} >Neno's Clinic</Navbar.Brand>
                            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                                <Navbar.Collapse id="responsive-navbar-nav">
                                    <Nav className="mr-auto">
                                    
                                    </Nav>
                                    <Nav>
                                    <Nav.Link className="mx-5" href="/addPatient" style={{ color: '#5bc0de'}}>Add Patient</Nav.Link>
                                    </Nav>
                            </Navbar.Collapse>
                </Navbar>

            <h2>Create Patient:</h2>
            <form>
                First Name: <input type="text" name="firstName" onChange={(event=>this.firstName=event.target.value)}></input>
                Last name: <input type="text" name="lastName" onChange={(event=>this.lastName=event.target.value)}></input>
                Age: <input type="text" name="age" onChange={(event=>this.age=event.target.value)}></input>
                <button onClick={this.handleSubmit.bind(this)}>Confirm</button>
            </form>

            <h3><Link to="/">GoBack</Link></h3>

        </div>)
    }
}

export default AddPatient;
