import React from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import '../App.css';
class ChartGenerator extends React.Component{

    state={
        clinicalData:[]
        
    }
    
 
    
    
    

    componentDidMount(){
        
        axios.get("http://localhost:8080/clinicalservices/api/clinicals/"+this.props.match.params.patientId+"/"+this.props.match.params.componentName).then(res=>{

            this.setState({ clinicalData: res.data });
        })

    }

    render(){
        var ime ="";
        var ime1="";
        var ime2="";

        if(this.props.match.params.componentName==="bp"){
             ime="Blood Pressure";
             ime1="Systolic";
             ime2="Dystolic";
        }else if(this.props.match.params.componentName==="hw"){
            ime="Height/Weight";
            ime1="Height";
            ime2="Weight"
        }else if(this.props.match.params.componentName==="heartrate"){
            ime="HeartRate";
            ime1="Beats Per Minute";
        }else{
            ime="Body Mass Index";
            ime1="kg/m2";
        }

        var Vreme = new Array();
        var myArray = new Array();
        this.state.clinicalData.map((item,index)=>{
            if(item.componentName==this.props.match.params.componentName){
            myArray.push(item.componentValue);
            Vreme.push(item.measuredDateTime)
            }
        })
        
        var VisokPritisok = new Array();
        var NizokPritisok = new Array();
        myArray.forEach(element => 
            VisokPritisok.push(element.split('/')[0])
            );

        myArray.forEach(element => 
            NizokPritisok.push(element.split('/')[1])
                 );


        function LineChart(){
            const type={

                type: 'line'
            }
            const data = {
                labels:Vreme,
                datasets:[
                    {
                        label:ime1,
                        data:VisokPritisok,
                        borderColor: ['rgba(255,206,86 ,0.2)'],     
                        backgroundColor:['rgba(240,128,128 ,0.5)'],                
                        pointBackgroundColor:'rgba(255,206,86 ,0.2)',
                        pointBorderColor:'rgba(255,206,86 ,0.2))'
                    },
                    {
                        label:ime2,
                        data:NizokPritisok,
                        borderColor: ['rgba(54,162,235 ,0.2)'],
                        backgroundColor:['rgba(54,162,235 ,1)'], 
                        pointBackgroundColor:'rgba(54,162,235 ,0.2)',
                        pointBorderColor:'rgba(54,162,235 ,0.2)'
                    }
                ]
               
            }
            const options ={
                title:{
                    display: true,
                    color:('dark','#090908'),
                    text:ime,
                    fontSize:"40",
                    bold:true

                }

                

            }
            return(
                <Line type={type} data={data} options={options}/>
            )
        }
       
        return (<div>    

                <div className='chart'>
                    <LineChart/>
                </div>

        </div>);
    }
}



export default ChartGenerator;
