import React from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';
import {toast} from 'react-toastify'
class Home extends React.Component{

  state={
    patientData:[]
  }
    
 

      componentDidMount(){
        axios.get('http://localhost:8080/clinicalservices/api/patients').then(res=>{
          const patientData = res.data;
          this.setState({patientData})

        })

      }

      handleSubmit(id){
      

        axios.get('http://localhost:8080/clinicalservices/api/patients/delete'+this.props.match.params.patientId).then(res=>{
            toast("patient deleted sucessfully!",{autoClose:2000,position:toast.POSITION.BOTTOM_CENTER})
        })


    }
      

      render(){
        return (<div>
        <h2>Patient:</h2>
        <table align='center'>
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Age</th>
            </tr>
          </thead> 
          <tbody>
            {this.state.patientData.map(patient=><RowCreator item={patient} key={patient.id} />)}
          </tbody>
        </table>
        <Link to={'/addPatient'}><font size="5">Register Patient</font></Link>
        </div>)
      }
    }

    class RowCreator extends React.Component {
  render() {
    var patient = this.props.item;
    return (
      <tr>
        <td>{patient.firstName}</td>
        <td>{patient.lastName}</td>
        <td>{patient.age}</td>
        <td>
          <Link to={"/patientDetails/" + patient.id}>Add Data</Link>
        </td>
        <td>
          <Link to={"/analyze/" + patient.id}>Analyze</Link>
        </td>
        <td>
        <span onClick={handleSubmit()}>More</span>
        </td>
      </tr>
    );
  }
}

export default Home;
