import React, {Component} from 'react';
import CanvasJSReact from '../assets/canvasjs.react';
//var CanvasJSReact = require('./canvasjs.react');
import axios from 'axios';
//react bootstrap components
import CardDeck from "react-bootstrap/CardDeck";
import Card from "react-bootstrap/Card";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;
class ChartGenerator extends React.Component{

    state={
        clinicalData:[]
    }

    componentDidMount(){
        axios.get("http://localhost:8080/clinicalservices/api/clinicals/1/bp").then(res=>{
            this.setState(res.data);
        })

    }


    render(){
        const options = {
            animationEnabled: true,	
            title:{
                text: "Number of New Customers"
            },
            
            toolTip: {
                shared: true
            },
            data: [{
                type: "spline",
                name: "2016",
                showInLegend: true,
                dataPoints: [
                    { y: 155, label: "Jan" },
                    { y: 150, label: "Feb" },
                    { y: 152, label: "Mar" },
                    { y: 148, label: "Apr" },
                    { y: 142, label: "May" },
                    { y: 150, label: "Jun" },
                    { y: 146, label: "Jul" },
                    { y: 149, label: "Aug" },
                    { y: 153, label: "Sept" },
                    { y: 158, label: "Oct" },
                    { y: 154, label: "Nov" },
                    { y: 150, label: "Dec" }
                ]
            },
            {
                type: "spline",
                name: "2017",
                showInLegend: true,
                dataPoints: [
                    { y: 172, label: "Jan" },
                    { y: 173, label: "Feb" },
                    { y: 175, label: "Mar" },
                    { y: 172, label: "Apr" },
                    { y: 162, label: "May" },
                    { y: 165, label: "Jun" },
                    { y: 172, label: "Jul" },
                    { y: 168, label: "Aug" },
                    { y: 175, label: "Sept" },
                    { y: 170, label: "Oct" },
                    { y: 165, label: "Nov" },
                    { y: 169, label: "Dec" }
                ]
            }]
    }

        return (<div>
                
               <CanvasJSChart options = {options} 
				/* onRef={ref => this.chart = ref} */
			/>
			{/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
            <h2>Test Data:</h2>
            <>
				<Container fluid={true}>
					<Row>
						<CardDeck className=' no-gutters '>
							{data.map((postData) => {
								console.log(postData);
								return (
									<Card  key={postData.id}>
										<Card.Img variant="top" src={postData.image} />
										<Card.Body>
											<Card.Title className={style.tile}>
												{postData.title}
											</Card.Title>
											<Card.Subtitle className={style.tag}>
												{postData.tag + " "}
											</Card.Subtitle>

											<Card.Text className={style.para}>
												{postData.body}
											</Card.Text>
										</Card.Body>
									</Card>
								);
							})}
						</CardDeck>
					</Row>
				</Container>
			</>
        </div>);
    }
}


export default ChartGenerator;
