import React from 'react';
import axios from 'axios';
class ChartGenerator extends React.Component{

    state={
        clinicalData:[]
    }

    componentDidMount(){
        axios.get("http://localhost:8080/clinicalservices/api/clinicals/1/bp").then(res=>{
            this.setState(res.data);
        })

    }


    render(){
        return (<div>
            <h2>Clinical Report:</h2>
            {this.state.clinicalData.map(eachEntry=><TableCreator item={eachEntry} patientId={this.state.id} key={eachEntry.id} />)}

        </div>)
    }
}


class TableCreator extends React.Component{
    render(){
        var eachEntry = this.props.item;
        var patientId = this.props.patientId;
        return <div>
            <table>
                <tbody>
                <tr><td><b>{eachEntry.componentName}</b></td></tr>
                <tr>
                    <td>{eachEntry.componentName}</td>
                    <td>{eachEntry.componentValue}</td>
                    <td>{eachEntry.measuredDateTime}</td>
                    <td>
                        <Link to ={"/chart/"+eachEntry.componentName+"/"+patientId}><img src={process.env.PUBLIC_URL + '/Picture.png'} width="20" height="15"/></Link>
                    </td>
                    
                </tr>
                </tbody>
            </table>
        </div>
    }
}


export default ChartGenerator;
