import React,{useEffect} from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';
import {toast} from 'react-toastify';
import { Button, Container, Row, Col, Alert, } from 'react-bootstrap';
class Home extends React.Component{

  state={
    patientData:[]
  }
    
 

      componentDidMount(){
        axios.get('http://localhost:8080/clinicalservices/api/patients').then(res=>{
          const patientData = res.data;
          this.setState({patientData})

        })

      }


    
      

      render(){
        return (
        
        <div>
        <h2>Patient:</h2>
        <table align='center'>
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Age</th>
            </tr>
          </thead> 
          <tbody>
            {this.state.patientData.map(patient=><RowCreator item={patient} key={patient.id} />)}
          </tbody>
        </table>
        <Link to={'/addPatient'}><font size="5">Register Patient</font></Link>
            [
              'primary'
            ].
              map((variant, idx) => (
              <Alert key={idx} variant={variant}>
                This is a {variant} alert—check it out!
              </Alert>
            ));
        </div>
        )
      }
    }

    class RowCreator extends React.Component {
      
      render() {

      
      function povikaj(id){

     
      
        axios.get('http://localhost:8080/clinicalservices/api/patients/delete/'+id).then(res=>{
          toast("Patient Deleted Sucessfully, pls wait 3 seconds",{autoClose:3000,position:toast.POSITION.BOTTOM_CENTER})
          
          setTimeout(function() {
            window.location.reload();
            }.bind(this), 3000);
        })
        
             
      }

       

    var patient = this.props.item;

    return (
      <tr>
        <td>{patient.firstName}</td>
        <td>{patient.lastName}</td>
        <td>{patient.age}</td>
        <td>
          <Link to={"/patientDetails/" + patient.id}>Add Data</Link>
        </td>
        <td>
          <Link to={"/analyze/" + patient.id}>Analyze</Link>
        </td>
        <td>
        <Link to="/" onClick={()=>povikaj(patient.id)}>Delete</Link>
        </td>
      </tr>
    );
  }
}

export default Home;
