import React,{useEffect} from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';
import {toast} from 'react-toastify';
import { Button, Container, Row, Col, Alert, Table, Navbar, Nav, NavDropdown, Carousel } from 'react-bootstrap';
import '../css/Home.css'
import image from '../assets/iamges/61808.jpg'
import slika from '../assets/iamges/slika1.jpg'
class Home extends React.Component{

  state={
    patientData:[]
  }
    
 

      componentDidMount(){
        axios.get('http://localhost:8080/clinicalservices/api/patients').then(res=>{
          const patientData = res.data;
          this.setState({patientData})

        })

      }


    
      

      render(){
        var variant="";
        return (
        <div className='Login-component'>
           
          
                      <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
              <Navbar.Brand className="mx-5" href="/" style={{ color: '#5bc0de'}} >Neno's Clinic</Navbar.Brand>
              <Navbar.Toggle aria-controls="responsive-navbar-nav" />
              <Navbar.Collapse id="responsive-navbar-nav">
                <Nav className="mr-auto">
                 
                </Nav>
                <Nav>
                  <Nav.Link className="mx-5" href="/addPatient" style={{ color: '#5bc0de'}}>Add Patient</Nav.Link>
                </Nav>
              </Navbar.Collapse>
            </Navbar>
        
        <Container fluid>
        <Row className="justify-content-center mt-5">  
        <Col md="auto" xs="auto" sm="auto" lg="auto" xl="auto" xxl="auto"><h2 class="mt-5">Patient:</h2></Col>
        </Row>
        </Container>
        <Container fluid mb-1>
        <Row className="justify-content-center mt-5">
          <Col md="auto" xs="auto" sm="auto" lg="auto" xl="auto" xxl="auto">
        <Table xs="auto" sm="auto" lg="auto" xl="auto" xxl="auto" responsive striped bordered hover variant="dark">
         
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Age</th>
              <th colSpan="3">Change Data</th>
            </tr>
          </thead> 
          <tbody>
            {this.state.patientData.map(patient=><RowCreator item={patient} key={patient.id} />)}
          </tbody>
          
        </Table>
        </Col>
        </Row>
                </Container >
                  <Carousel className="mt-5 d-block w-40 justify-content-center">
                  <Carousel.Item className="justify-content-center">
                    
                    <img
                      className="d-block w-40 justify-content-center w-50 h-20"
                      src={slika}
                      alt="First slide"
                    />
                    <Carousel.Caption>
                      <h3>First slide label</h3>
                      <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                    </Carousel.Caption>
                  </Carousel.Item>
                  <Carousel.Item>
                    <img
                      className="d-block w-100"
                      src="holder.js/800x400?text=Second slide&bg=282c34"
                      alt="Second slide"
                    />

                    <Carousel.Caption>
                      <h3>Second slide label</h3>
                      <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                    </Carousel.Caption>
                  </Carousel.Item>
                  <Carousel.Item>
                    <img
                      className="d-block w-100"
                      src="holder.js/800x400?text=Third slide&bg=20232a"
                      alt="Third slide"
                    />

                    <Carousel.Caption>
                      <h3>Third slide label</h3>
                      <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
                    </Carousel.Caption>
                  </Carousel.Item>
                </Carousel>     
        </div>
        )
      }
    }

    class RowCreator extends React.Component {
      
      render() {

      
      function povikaj(id){

     
      
        axios.get('http://localhost:8080/clinicalservices/api/patients/delete/'+id).then(res=>{
          toast("Patient Deleted Sucessfully, pls wait 3 seconds",{autoClose:3000,position:toast.POSITION.BOTTOM_CENTER})
          
          setTimeout(function() {
            window.location.reload();
            }.bind(this), 3000);
        })
        
             
      }

       

    var patient = this.props.item;

    return (
      <tr>
        <td>{patient.firstName}</td>
        <td>{patient.lastName}</td>
        <td>{patient.age}</td>
        <td>
          <Link to={"/patientDetails/" + patient.id}><Button variant="info">Add Data</Button></Link>
        </td>
        <td>
          <Link to={"/analyze/" + patient.id}><Button variant="info">Analyze</Button></Link>
        </td>
        <td>
        <Button variant="info" onClick={()=>povikaj(patient.id)}>Delete</Button>
        </td>
      </tr>
    );
  }
}

export default Home;
