import React from 'react';
import axios from 'axios';
import Chart from 'Chart.js';
class ChartGenerator extends React.Component{

    state={
        clinicalData:[],
        chartData:{}
    }
    

    

    

    componentDidMount(){
        axios.get("http://localhost:8080/clinicalservices/api/clinicals/"+this.props.match.params.patientId+"/"+this.props.match.params.componentName).then(res=>{

            this.setState({ clinicalData: res.data });
        })

        this.getChartData();

    }
    getChartData(){
        // Ajax calls here
        this.setState({
          chartData:{
            labels: ['Boston', 'Worcester', 'Springfield', 'Lowell', 'Cambridge', 'New Bedford'],
            datasets:[
              {
                label:'Population',
                data:[
                  617594,
                  181045,
                  153060,
                  106519,
                  105162,
                  95072
                ],
                backgroundColor:[
                  'rgba(255, 99, 132, 0.6)',
                  'rgba(54, 162, 235, 0.6)',
                  'rgba(255, 206, 86, 0.6)',
                  'rgba(75, 192, 192, 0.6)',
                  'rgba(153, 102, 255, 0.6)',
                  'rgba(255, 159, 64, 0.6)',
                  'rgba(255, 99, 132, 0.6)'
                ]
              }
            ]
          }
        });
      }


    render(){
       
        return (<div>         
            
            <Chart chartData={this.state.chartData} location="Massachusetts" legendPosition="bottom"/>
            

        </div>);
    }
}



export default ChartGenerator;
